import React, { useState, useEffect } from "react";

import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateNote from "./CreateNote";
import initNotes from "../notes";
import axios from 'axios';
let id = 5; // keeps track of id's

function App() {
  const [notes, setNotes] = useState([]); // dynamic usestate to keep track of current notes

  useEffect(() => {
    axios.get(`http://localhost:8000/notes`).then((res) => {
      setNotes(res.data);
    }).catch((err) => {
      console.log(err);
    });
  });

  function handleCreateNode(tit, msg) {
    // function to be passed to createNote() to add notes based on user inputs
    axios.post(`http://localhost:8000/notes`, {tit, msg}).then((res) => {
      console.log(res.data);
      setNotes([...notes, ])
    })


    // setNotes([
    //   ...notes, // concatenate new entry to previous notes array
    //   {
    //     key: id++,
    //     title: tit,
    //     content: msg,
    //   },
    // ]);
  }

  const handleDeleteNote = (id) => {
    axios.delete(`http://localhost:8000/notes/${id}`).then((res) => {
      console.log(res.data);
      setNotes(notes.filter((note) => note.key !== id));
    })
    console.log("delete note function called");
  }

  function oldHandleDeleteNode(id) {
    // function to be passed to Note.jsx to delete notes based on id
    setNotes(notes.filter((note) => note.key !== id));
    console.log(notes.length);
  }

  function addNote(notes) {
    // function to map all note entries in array to actual notes
    return (
      <Note
        key={notes.key}
        id={notes.key}
        title={notes.title}
        content={notes.content}
        deleteNote={handleDeleteNote}
      />
    );
  }

  return (
    <div className="pageDisplay">
      <Header />
      <CreateNote updateNotes={handleCreateNode} />
      <div className="noteList">
        <dl>{notes.map(addNote)}</dl>
      </div>
      <Footer />
    </div>
  );
}

export default App;
